import React, { useState } from 'react';
import styled from 'styled-components';
import 'bootstrap/dist/css/bootstrap.min.css';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import $ from 'jquery';
import { Link } from 'react-router-dom';
import {Modal, Button} from 'react-bootstrap';
import {SHOTS} from '../data/shots'

function Collections() {
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const [likehover, setLikeHover] = useState(null);

    const like_img = {
        inactive: '/images/likesicon.svg',
        active: '/images/likedicon.svg',
    }
    return (
        <div>
            <CollectionContainer>
                <Row>
                    {SHOTS.map((shot, index) =>(
                        <Col xs={6} md={3} lg={3} className="shots_col">

                                <Shot onClick={handleShow}>
                                    <ThumbnailContainer>
                                        <Thumbnail>
                                            <img src={shot.thumbnail}/>
                                        </Thumbnail>
                                        <ShotInfo>
                                            <ShotActions className="shot_actions">
                                                <ShotTitle>
                                                    <h6>{shot.title}</h6>
                                                </ShotTitle>
                                                <ShotAction>
                                                    <ShotSave>
                                                        <img src="/images/saveicon.svg"/>
                                                    </ShotSave>
                                                    <ShotLike>
                                                        <img src="/images/likesicon.svg"/>
                                                    </ShotLike>
                                                </ShotAction>
                                            </ShotActions>
                                        </ShotInfo>
                                    </ThumbnailContainer>
                                    <ShotSummary>
                                        <ShotCreatorInfo>
                                                <img src={shot.profile_picture}/>
                                                <h6>{shot.user}</h6>
                                                <p className="shot_creator">{shot.type}</p>
                                        </ShotCreatorInfo>
                                        <ShotEngagement>
                                            <ShotLikes
                                            >
                                                {likehover==index ? <img src={like_img.active}
                                                    onMouseEnter={() => {
                                                        setLikeHover(index);
                                                    }}

                                                    onMouseOut={() => {
                                                        setLikeHover(null);
                                                    }}
                                                /> 
                                                : 
                                                <img src={like_img.inactive}
                                                    onMouseEnter={() => {
                                                        setLikeHover(index);
                                                    }}

                                                    onMouseOut={() => {
                                                        setLikeHover(null);
                                                    }}
                                                />}

                                                <p>{shot.likes}</p>
                                            </ShotLikes>
                                            <ShotViews>
                                                <img src="/images/viewsicon.svg"/>
                                                <p>{shot.views}</p>
                                            </ShotViews>
                                        </ShotEngagement>
                                    </ShotSummary>
                                </Shot>
                        </Col>
                    ))}
                </Row>

                <Row>
                    <Col xs={12} md={12} lg={12}>
                        <LoginButtons>
                            <div className="buttons_div">
                                <button className="btn btn-primary" type="button">Sign up to continue</button>
                                <a>or Sign in</a>
                            </div>
                        </LoginButtons>
                    </Col>
                </Row>
                <Modal show={show} 
                    onHide={handleClose}
                    size="xl"
                    aria-labelledby="contained-modal-title-vcenter"
                    centered

                    style={{
                        background: 'rgba(134, 102, 255, 0.1)',
                        borderRadius: '10px',
                        maxWidth: '100%!important',
                        width: '100%',


                    }}

                >
                    <button>Close</button>
                    <div>Hello </div>
                </Modal>
            </CollectionContainer>

        </div>

    )
}

export default Collections


const CollectionContainer = styled.div`
    padding: 0px 67px;
    margin-bottom: 80px;
    position: relative;

    .shots_col {
        margin-top: 15px; 
    }

    .modal-dialog {
        max-width: 100%!important;
    }


`;


const Shot = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    position: relative;
    cursor: pointer;
    transition: all 250ms cubic-bezier(0.25, 0.46, 0.45, 0.94) 0s;
`;

const ThumbnailContainer = styled.div`
    display: flex;
    flex-direction: column;
    position: relative;
`;
const Thumbnail = styled.div`
    border-radius: 10px;
    img {
        border-radius: 10px;
        width: 100%;
    }
`;
const ShotInfo = styled.div`
`;
const ShotCreatorInfo = styled.div`
    display: flex;
    align-items: center;
    flex: 1;
    justify-content: flex-start;

    img {
        width: 25px;
        border-radius: 50%;
        height: 25px;
        margin-right: 10px;
    }

    h6 {
        color: rgba(0, 0, 0, 1);
        font-weight: 500;
        margin: 0;
        font-size: 15px;
        margin-right: 6px;
    }

    p {
        margin: 0;
        font-size: 10px;
        padding: 2px 6px;
        background: rgba(0, 0, 0, 0.2);
        border-radius: 4px;
        transition: all 250ms cubic-bezier(0.25, 0.46, 0.45, 0.94) 0s;
        color: #fff;
        &:hover {
            background: #EA4C89;
        }
