import React, { useState } from 'react';
import styled from 'styled-components';
import 'bootstrap/dist/css/bootstrap.min.css';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import $ from 'jquery';
import { Link } from 'react-router-dom';
import {Modal, Button} from 'react-bootstrap';
import {SHOTS} from '../data/shots'

function Collections() {
    const [modalShow, setModalShow] = useState(false);
    const [likehover, setLikeHover] = useState(null);

    const like_img = {
        inactive: '/images/likesicon.svg',
        active: '/images/likedicon.svg',
    }


    /* Open when someone clicks on the span element */
    function openNav() {
        document.getElementById("myNav").style.width = "100%";
    }

    /* Close when someone clicks on the "x" symbol inside the overlay */
    function closeNav() {
        document.getElementById("myNav").style.width = "0%";
    }
    return (
        <div>
            <CollectionContainer>
                <Row>
                    {SHOTS.map((shot, index) =>(
                        <Col xs={6} md={3} lg={3} className="shots_col">

                                <Shot data-toggle="modal" data-target="#exampleModal">
                                    <ThumbnailContainer>
                                        <Thumbnail>
                                            <img src={shot.thumbnail}/>
                                        </Thumbnail>
                                        <ShotInfo>
                                            <ShotActions className="shot_actions">
                                                <ShotTitle>
                                                    <h6>{shot.title}</h6>
                                                </ShotTitle>
                                                <ShotAction>
                                                    <ShotSave>
                                                        <img src="/images/saveicon.svg"/>
                                                    </ShotSave>
                                                    <ShotLike>
                                                        <img src="/images/likesicon.svg"/>
                                                    </ShotLike>
                                                </ShotAction>
                                            </ShotActions>
                                        </ShotInfo>
                                    </ThumbnailContainer>
                                    <ShotSummary>
                                        <ShotCreatorInfo>
                                                <img src={shot.profile_picture}/>
                                                <h6>{shot.user}</h6>
                                                <p className="shot_creator">{shot.type}</p>
                                        </ShotCreatorInfo>
                                        <ShotEngagement>
                                            <ShotLikes
                                            >
                                                {likehover==index ? <img src={like_img.active}
                                                    onMouseEnter={() => {
                                                        setLikeHover(index);
                                                    }}

                                                    onMouseOut={() => {
                                                        setLikeHover(null);
                                                    }}
                                                /> 
                                                : 
                                                <img src={like_img.inactive}
                                                    onMouseEnter={() => {
                                                        setLikeHover(index);
                                                    }}

                                                    onMouseOut={() => {
                                                        setLikeHover(null);
                                                    }}
                                                />}

                                                <p>{shot.likes}</p>
                                            </ShotLikes>
                                            <ShotViews>
                                                <img src="/images/viewsicon.svg"/>
                                                <p>{shot.views}</p>
                                            </ShotViews>
                                        </ShotEngagement>
                                    </ShotSummary>
                                </Shot>
                        </Col>
                    ))}
                </Row>

                <Row>
                    <Col xs={12} md={12} lg={12}>
                        <LoginButtons>
                            <div className="buttons_div">
                                <button className="btn btn-primary" type="button">Sign up to continue</button>
                                <a>or Sign in</a>
                            </div>
                        </LoginButtons>
                    </Col>
                </Row>
                <ModalContainer>
                <div id="myNav" class="overlay">


                    <a href="javascript:void(0)" class="closebtn" onclick="closeNav()">&times;</a>


                    <div class="overlay-content">
                    <a href="#">About</a>
                    <a href="#">Services</a>
                    <a href="#">Clients</a>
                    <a href="#">Contact</a>
                    </div>

                    </div>

                    </ModalContainer>
            </CollectionContainer>

        </div>

    )
}

export default Collections


const CollectionContainer = styled.div`
    padding: 0px 67px;
    margin-bottom: 80px;
    position: relative;

    .shots_col {
        margin-top: 15px; 
    }

    .modal-content {
        width: max-content;
    }

`;

const ModalContainer = styled.div`
    /* The Overlay (background) */
    .overlay {
    /* Height & width depends on how you want to reveal the overlay (see JS below) */   
    height: 100%;
    width: 0;
    position: fixed; /* Stay in place */
    z-index: 1; /* Sit on top */
    left: 0;
    top: 0;
    background-color: rgb(0,0,0); /* Black fallback color */
    background-color: rgba(0,0,0, 0.9); /* Black w/opacity */
    overflow-x: hidden; /* Disable horizontal scroll */
    transition: 0.5s; /* 0.5 second transition effect to slide in or slide down the overlay (height or width, depending on reveal) */
    }

    /* Position the content inside the overlay */
    .overlay-content {
